import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { Store } from "../Store";
import { getError } from "../utilis";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";


const CreateMessageScreen = () => {
  const navigate = useNavigate();
  const [subject, setSubject] = useState("");
  const [content, setContent] = useState("");
  const [receiver, setReceiver] = useState("");
  const { state } = useContext(Store);
  const { userInfo } = state;

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const { data } = await axios.post(
        "/api/addmessage/",
        { subject, content, receiver },
        { headers: { Authorization: `Bearer ${userInfo.access}` } }
      );
      toast.success("message sent successfully");
      console.log("New message created:", data);
      navigate(`/messages/`);

      // Handle success and update message list if needed
    } catch (err) {
      toast.error("Receiver does not exist");
      console.error("Error creating message:", err.response.data);
      // Handle error if needed
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="receiver">
        <Form.Label>Receiver</Form.Label>
        <Form.Control
          type="text"
          placeholder="receiver"
          required
          value={receiver}
          onChange={(e) => setReceiver(e.target.value)}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="Subject">
        <Form.Label>Subject</Form.Label>
        <Form.Control
          type="text"
          placeholder="Subject"
          required
          value={subject}
          onChange={(e) => setReceiver(e.target.value)}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="Content">
        <Form.Label>Content</Form.Label>
        <Form.Control
          type="text"
          placeholder="Content"
          required
          value={content}
          onChange={(e) => setReceiver(e.target.value)}
        />
      </Form.Group>
      
        
      <div className="mb-3">
          <Button type="submit" >Sign In</Button>
        </div>
    </Form>
  );
};

export default CreateMessageScreen;
