import React, { useContext, useEffect, useReducer } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Store } from "../Store";
import { Helmet } from "react-helmet-async";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import Button from "react-bootstrap/Button";
import { toast } from "react-toastify";
import { getError } from "../utilis";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      console.log(state);
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      console.log(state, action.payload);
      return { ...state, messages: action.payload, loading: false };
    case "FETCH_FAIL":
      console.log(state, action.type, action.payload);
      return { ...state, error: action.payload, loading: false };
    case "DELETE_REQUEST":
      return { ...state, loadingDelete: true, successDelete: false };
    case "DELETE_SUCCESS":
      return {
        ...state,
        loadingDelete: false,
        successDelete: true,
      };
    case "DELETE_FAIL":
      return { ...state, loadingDelete: false };
    case "DELETE_RESET":
      return { ...state, loadingDelete: false, successDelete: false };

    default:
      return state;
  }
};

const MessagesBoxScreen = () => {
  const { state } = useContext(Store);
  const { userInfo } = state;
  console.log(userInfo.access);
  const navigate = useNavigate();
  const [{ loading, error, messages, loadingDelete, successDelete }, dispatch] = useReducer(reducer, {
    messages: [],
    loading: true,
    error: "",
  });

  const deleteHandler = async (message) => {
    console.log(message)
    if (window.confirm("Are you sure you want to delete this message?")) {
      try {
        dispatch({ type: "DELETE_REQUEST" });
        await axios.delete(`http://127.0.0.1:8000/api/delete-message/${message.id}/`, {
          headers: { Authorization: `Bearer ${userInfo.access}` },
        });
        toast.success("message deleted successfully");
        dispatch({ type: "DELETE_SUCCESS" });
      } catch (err) {
        toast.error(getError(err));
        dispatch({
          type: "DELETE_FAIL",
        });
      }
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get("http://127.0.0.1:8000/api/messages/", {
          headers: { Authorization: `Bearer ${userInfo.access}` },
        });
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
        console.log(result.data);
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    if (successDelete) {
      dispatch({ type: "DELETE_RESET" });
    } else {
      fetchData();
    }
  }, [userInfo, successDelete]);

  return (
    <div>
      <Helmet>
        <title>Messages</title>
      </Helmet>

      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (

        <><nav>
              <div class="nav nav-tabs" id="nav-tab" role="tablist">
                <button class="nav-link active" id="nav-home-tab" data-bs-toggle="tab" data-bs-target="#nav-home" type="button" role="tab" aria-controls="nav-home" aria-selected="true">Received</button>
                <button class="nav-link" id="nav-profile-tab" data-bs-toggle="tab" data-bs-target="#nav-profile" type="button" role="tab" aria-controls="nav-profile" aria-selected="false">Profile</button>
                <button class="nav-link" id="nav-contact-tab" data-bs-toggle="tab" data-bs-target="#nav-contact" type="button" role="tab" aria-controls="nav-contact" aria-selected="false">Contact</button>
              </div>
            </nav><div class="tab-content" id="nav-tabContent">
                <div class="tab-pane fade show active" id="nav-home" role="tabpanel" aria-labelledby="nav-home-tab">...</div>
                <div class="tab-pane fade" id="nav-profile" role="tabpanel" aria-labelledby="nav-profile-tab">...</div>
                <div class="tab-pane fade" id="nav-contact" role="tabpanel" aria-labelledby="nav-contact-tab">...</div>
              </div><table className="table">
                <thead>
                  <tr>
                    <th>SUBJECT</th>
                    <th>CONTENT</th>
                    <th>DATE</th>
                    <th>SENDER</th>
                    <th>RECEIVER</th>
                    <th><Button
                      type="button"
                      variant="success"
                      onClick={() => navigate('/createmessage')}
                    >
                      Send
                    </Button></th>
                  </tr>
                </thead>
                <tbody>
                  {messages.map((message) => (
                    <tr key={message.id}>
                      <td>{message.subject}</td>
                      <td>{message.content}</td>
                      <td>{message.creation_date}</td>
                      <td>{message.sender}</td>
                      <td>{message.receiver}</td>
                      <td>
                        <Button
                          type="button"
                          variant="dark"
                          onClick={() => navigate(`/admin/user/${message._id}`)}
                        >
                          Edit
                        </Button>
                        &nbsp;
                        <Button
                          type="button"
                          variant="danger"
                          onClick={() => deleteHandler(message)}
                        >
                          Delete
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table></>
      )}
    </div>
  );
};

export default MessagesBoxScreen;
