import React from "react";
import { Container, Jumbotron } from "react-bootstrap";

const HomeScreen = () => {
  return (
    <Container className="mt-5">
      <Jumbotron>
        <h2>Welcome to the Message System</h2>
        <p>
          This is a simple message system application. You can use the
          navigation bar above to sign in or sign up, and then explore the
          messages under different tabs.
        </p>
        <p>
          The application allows you to send, receive, mark messages as read or
          unread, and more.
        </p>
      </Jumbotron>
    </Container>
  );
};

export default HomeScreen;
