import "./App.css";
import { useContext, useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { Store } from "./Store";
import MessagesBoxScreen from "./screens/MessagesBoxScreen";
import SignupScreen from "./screens/SignUpScreen";
import SigninScreen from "./screens/SignInScreen";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";
import { LinkContainer } from "react-router-bootstrap";
import Nav from "react-bootstrap/Nav";
import ProtectedRoute from "./components/ProtectedRoute";
import CreateMessageScreen from "./screens/CreateMessageScreen";
import { Tabs, Tab } from "react-bootstrap";

function App() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const [activeTab, setActiveTab] = useState("received"); // Default active tab

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const signoutHandler = () => {
    localStorage.removeItem("userInfo");
    ctxDispatch({ type: "USER_SIGNOUT" });
    window.location.href = "/signin";
  };

     return (
      <div className="App">
      <BrowserRouter>
        <ToastContainer position="top-center" limit={1} />
        <header>
          <Navbar bg="dark" variant="dark" expand="lg">
            <Container>
              <LinkContainer to="/">
                <Navbar.Brand>Message System</Navbar.Brand>
              </LinkContainer>
              <Nav>
                {userInfo ? (
                  <>
                    <Nav.Link
                      as={Link}
                      to="/createmessage"
                      eventKey="create" // Add eventKey for the "Create Message" tab
                      onSelect={() => handleTabChange("create")}
                    >
                      Create Message
                    </Nav.Link>
                    <Nav.Link
                      as={Link}
                      to="/messages"
                      eventKey="received"
                      onSelect={() => handleTabChange("received")}
                    >
                      Received Messages
                    </Nav.Link>
                    <Nav.Link
                      as={Link}
                      to="/messages"
                      eventKey="sent"
                      onSelect={() => handleTabChange("sent")}
                    >
                      Sent Messages
                    </Nav.Link>
                    <Nav.Link
                      as={Link}
                      to="/messages"
                      eventKey="read"
                      onSelect={() => handleTabChange("read")}
                    >
                      Read Messages
                    </Nav.Link>
                    <Nav.Link
                      as={Link}
                      to="/unread"
                      eventKey="unread"
                      onSelect={() => handleTabChange("unread")}
                    >
                      Unread Messages
                    </Nav.Link>
                    <Nav.Link
                      as={Link}
                      to="#signout"
                      onClick={signoutHandler}
                    >
                      Sign Out
                    </Nav.Link>
                  </>
                ) : (
                  <Nav.Link as={Link} to="/signin">
                    Sign In
                  </Nav.Link>
                )}
              </Nav>
            </Container>
          </Navbar>
        </header>
        <main>
          <Routes>
            <Route path="/signin" element={<SigninScreen />} />
            <Route path="/messages" element={<MessagesBoxScreen />} />
            <Route
              path="/createmessage"
              element={
                <ProtectedRoute>
                  <CreateMessageScreen />
                </ProtectedRoute>
              }
            />
            <Route path="/" element={<SignupScreen />} />
          </Routes>
        </main>
        <footer>
          <div className="text-center">@2023 All rights reserved</div>
        </footer>
      </BrowserRouter>
    </div>
  );
