    <div className="App">
      <BrowserRouter>
        <ToastContainer position="top-center" limit={1} />
        <header>
          <Navbar bg="dark" variant="dark" expand="lg">
            <Container>
              <LinkContainer to="/signup">
                <Navbar.Brand> Message System</Navbar.Brand>
              </LinkContainer>
              {!userInfo ? (
                <Nav>
                  <Link className="nav-link" to="/signin">
                    Sign In
                  </Link>
                </Nav>
              ) : (
                <>
                  <span>{username}</span>
                  <Nav>
                    <Link
                      className="nav-link"
                      to="#signout"
                      onClick={signoutHandler}
