import "./App.css";
import { useContext, useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { Store } from "./Store";
import MessagesBoxScreen from "./screens/MessagesBoxScreen";
import SignupScreen from "./screens/SignUpScreen";
import SigninScreen from "./screens/SignInScreen";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";
import { LinkContainer } from "react-router-bootstrap";
import Nav from "react-bootstrap/Nav";

function App() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;

  const signoutHandler = () => {
    localStorage.removeItem("accessToken");
    ctxDispatch({ type: "USER_SIGNOUT" });
    window.location.href = "/signin";
  };

  return (
    <div className="App">
      <BrowserRouter>
        <ToastContainer position="top-center" limit={1} />
        <header>
          <Navbar bg="dark" variant="dark" expand="lg">
            <Container>
              <LinkContainer to="/">
                <Navbar.Brand> Message System</Navbar.Brand>
              </LinkContainer>
              <Nav>
                <Link className="nav-link" to="/signin">
                  Sign In
                </Link>
              </Nav>
              <Nav>
                <Link
                  className="nav-link"
                  to="#signout"
                  onClick={signoutHandler}
                >
                  Sign Out
                </Link>
              </Nav>
            </Container>
          </Navbar>
        </header>
        <main>
          <Container>
            <Routes>
              <Route path="/signin" element={<SigninScreen />} />
              <Route path="/messages" element={<MessagesBoxScreen />} />

              <Route exact path="/" element={<SignupScreen />} />
            </Routes>
          </Container>
        </main>
        <footer>
          <div className="text-center">@2023 All rights reserved</div>
        </footer>
      </BrowserRouter>
    </div>
  );
}

export default App;
