import React, { useContext, useEffect, useState } from "react";
import { Tabs, Tab } from "react-bootstrap";
import { Store } from "../Store";
import MessagesBoxScreen from "../screens/MessagesBoxScreen";
import ProtectedRoute from "../components/ProtectedRoute";
import jwt_decode from "jwt-decode";


const MessageTabs = ({ activeTab, handleTabChange }) => {
  const { state } = useContext(Store);
  const { userInfo } = state;
  const [username, setUsername] = useState('');


  useEffect(() => {
    // Check if userInfo is available and not empty
    if (userInfo && userInfo.access) {
      // Decode the JWT token
      const decodedToken = jwt_decode(userInfo.access);
      // Extract the username from the decoded token
      const decodedUsername = decodedToken.username;
      // Set the username state
      setUsername(decodedUsername);
    }
  }, [userInfo]);


  return (
    <Tabs
      defaultActiveKey={activeTab}
      onSelect={(tab) => handleTabChange(tab)}
      animation={false}
      id="noanim-tab-example"
    >
      {userInfo && (
        <>
          <Tab eventKey="received" title="Received Messages">
            {activeTab === "received" && (
              <ProtectedRoute>
                <MessagesBoxScreen messageType="received" />
              </ProtectedRoute>
            )}
          </Tab>
          <Tab eventKey="sent" title="Sent Messages">
            {activeTab === "sent" && (
              <ProtectedRoute>
                <MessagesBoxScreen messageType="sent" />
              </ProtectedRoute>
            )}
          </Tab>
          <Tab eventKey="read" title="Read Messages">
            {activeTab === "read" && (
              <ProtectedRoute>
                <MessagesBoxScreen messageType="read" />
              </ProtectedRoute>
            )}
          </Tab>
          <Tab eventKey="unread" title="Unread Messages">
            {activeTab === "unread" && (
              <ProtectedRoute>
                <MessagesBoxScreen messageType="unread" />
              </ProtectedRoute>
            )}
          </Tab>
          <Tab eventKey="create" title="Create Message">
            {activeTab === "create" && (
              <ProtectedRoute>
                <MessagesBoxScreen messageType="create" />
              </ProtectedRoute>
            )}
          </Tab>
        </>
      )}
    </Tabs>
  );
};

export default MessageTabs;
