import React, { useEffect, useReducer } from "react";
import axios from "axios";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      console.log(state);
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      console.log(state, action.payload);
      return { ...state, messages: action.payload, loading: false };
    case "FETCH_FAIL":
      console.log(state, action.type, action.payload);
      return { ...state, error: action.payload, loading: false };
    default:
      return state;
  }
};

const MessagesBoxScreen = () => {
  const [{ loading, error, messages }, dispatch] = useReducer(reducer, {
    messages: [],
    loading: true,
    error: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get("http://127.0.0.1:8000/api/messages/");
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
        console.log(result.data);
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchData();
  }, []);

  return (
    <div>
        
      <div className="messages-list">
        {messages.map((message) => (
          <div key={message.id}>
            <h3>{message.subject}</h3>
            <p>{message.content}</p>
            <p>Created on: {message.creation_date}</p>
            <p>From: {message.sender}</p>
            <p>To: {message.receiver}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MessagesBoxScreen;
