import React, { useContext, useEffect, useReducer, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Store } from "../Store";
import { Helmet } from "react-helmet-async";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import Button from "react-bootstrap/Button";
import { toast } from "react-toastify";
import { getError } from "../utilis";
import CreateMessageScreen from "./CreateMessageScreen";
import Table from "react-bootstrap/Table";
import NoMessagesAlert from "../components/NoMessagesAlert";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      const messages = Array.isArray(action.payload) ? action.payload : [];
      return { ...state, messages, loading: false };
    case "FETCH_FAIL":
      return { ...state, error: action.payload, loading: false };
    case "DELETE_REQUEST":
      return { ...state, loadingDelete: true, successDelete: false };
    case "DELETE_SUCCESS":
      return {
        ...state,
        loadingDelete: false,
        successDelete: true,
      };
    case "DELETE_FAIL":
      return { ...state, loadingDelete: false };
    case "DELETE_RESET":
      return { ...state, loadingDelete: false, successDelete: false };

    default:
      return state;
  }
};

const MessagesBoxScreen = ({ messageType }) => {
  const { state } = useContext(Store);
  const { userInfo } = state;
  const accessToken = userInfo ? userInfo.access : null;
  const [loadingCreate, setLoadingCreate] = useState(false);
  console.log(accessToken);
  const navigate = useNavigate();
  const [{ loading, error, messages, loadingDelete, successDelete }, dispatch] =
    useReducer(reducer, {
      messages: [],
      loading: true,
      error: "",
    });

  const markReadHandler = async (message) => {
    console.log(message);
    try {
      dispatch({ type: "FETCH_REQUEST" });
      await axios.get(`/api/read-a-message/${message.id}/`, {
        headers: { Authorization: `Bearer ${userInfo.access}` },
      });
      toast.success("message marked as read successfully");
      dispatch({ type: "FETCH_SUCCESS" });
    } catch (err) {
      toast.error(getError(err));
      dispatch({
        type: "FETCH_FAIL",
      });
    }
  };

  const deleteHandler = async (message) => {
    console.log(message);
    if (window.confirm("Are you sure you want to delete this message?")) {
      try {
        dispatch({ type: "DELETE_REQUEST" });
        await axios.delete(`/api/delete-message/${message.id}/`, {
          headers: { Authorization: `Bearer ${userInfo.access}` },
        });
        toast.success("message deleted successfully");
        dispatch({ type: "DELETE_SUCCESS" });
      } catch (err) {
        toast.error(getError(err));
        dispatch({
          type: "DELETE_FAIL",
        });
      }
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        let url = "";
        if (messageType === "create") {
          setLoadingCreate(true);
        } else if (messageType === "received") {
          url = "/api/received-messages/";
        } else if (messageType === "sent") {
          url = "/api/sent-messages/";
        } else if (messageType === "read") {
          url = "/api/read-messages/";
        } else if (messageType === "unread") {
          url = "/api/unread-messages/";
        }
        const result = await axios.get(url, {
          headers: { Authorization: `Bearer ${userInfo.access}` },
        });
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
        console.log(result.data);
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    if (successDelete) {
      dispatch({ type: "DELETE_RESET" });
    } else {
      fetchData();
    }
  }, [userInfo, successDelete]);

  return (
    <div>
      <Helmet>
        <title>Messages</title>
      </Helmet>

      {loadingCreate ? (
        <CreateMessageScreen />
      ) : loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : messages.length === 0 ? ( // Show NoMessagesAlert when messages array is empty
        <NoMessagesAlert />
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>SUBJECT</th>
              <th>CONTENT</th>
              <th>DATE</th>
              <th>SENDER</th>
              <th>RECEIVER</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {messages?.map((message) => (
              <tr key={message.id}>
                <td>{message.subject}</td>
                <td>{message.content}</td>
                <td>{message.creation_date}</td>
                <td>{message.sender}</td>
                <td>{message.receiver}</td>
                <td>
                  <Button
                    type="button"
                    variant="dark"
                    onClick={() => markReadHandler(message)}
                  >
                    Mark as Read
                  </Button>
                  &nbsp;
                  <Button
                    type="button"
                    variant="danger"
                    onClick={() => deleteHandler(message)}
                  >
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default MessagesBoxScreen;
