import React, { useContext, useEffect, useReducer } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Store } from "../Store";
import { Helmet } from "react-helmet-async";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      console.log(state);
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      console.log(state, action.payload);
      return { ...state, messages: action.payload, loading: false };
    case "FETCH_FAIL":
      console.log(state, action.type, action.payload);
      return { ...state, error: action.payload, loading: false };
    default:
      return state;
  }
};

const MessagesBoxScreen = () => {
  const { state } = useContext(Store);
  const { userInfo } = state;
  console.log(userInfo.access)
  const navigate = useNavigate();
  const [{ loading, error, messages }, dispatch] = useReducer(reducer, {
    messages: [],
    loading: true,
    error: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get("http://127.0.0.1:8000/api/messages/",
        { headers: { Authorization: `Bearer ${userInfo.access}` } }
        );
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
        console.log(result.data);
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchData();
  }, [userInfo]);

  return (
    <div>
      <Helmet>
        <title>Messages</title>
      </Helmet>
      
      
    </div>
  );
};

export default MessagesBoxScreen;

