import axios from 'axios';

export const getError = (error) => {
    return error.response && 
    error.response.data.message
    ? error.response.data.message
    : error.message;
};


// Function to get the CSRF token from cookies
const getCSRFCookie = (name) => {
  const value = `; ${document.cookie}`;
  const parts = value.split(`; ${name}=`);
  if (parts.length === 2) return parts.pop().split(';').shift();
};

// Function to get the access token from localStorage
const getAccessToken = () => {
  const userInfo = JSON.parse(localStorage.getItem('userInfo'));
  return userInfo ? userInfo.access : null;
};

const api = axios.create({
  baseURL: '/api/', // Set the base URL for all requests
  headers: {
    'Content-Type': 'application/json',
    'X-CSRFToken': getCSRFCookie('csrftoken'), // Include the CSRF token in the headers
    'Authorization': `Bearer ${getAccessToken()}`, // Include the access token in the headers
  },
});

export default api;
