import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { Store } from "../Store";


const CreateMessageScreen = () => {
  const [subject, setSubject] = useState("");
  const [content, setContent] = useState("");
  const [receiver, setReceiver] = useState("");
  const [users, setUsers] = useState([]);
  const { state } = useContext(Store);
  const { userInfo } = state;

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        'http://127.0.0.1:8000/api/messages/',
        { subject, content, receiver },
        { headers: { Authorization: `Bearer ${userInfo.access}` } }
      );

      console.log('New message created:', response.data);
      // Handle success and update message list if needed
    } catch (error) {
      console.error('Error creating message:', error.message);
      // Handle error if needed
    }
  };

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const {data} = await axios.get('http://127.0.0.1:8000/api/users/');
        setUsers(data);
      } catch (error) {
        console.error('Error fetching users:', error.message);
      }
    };
    fetchUsers();
  }, []);

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="receiver">Select Receiver:</label>
        <input
          id="receiver"
          name="receiver"
          value={receiver}
          onChange={(e) => setReceiver(e.target.value)}
        >
          
        </input>
      </div>
      <div>
        <input
          type="text"
          placeholder="Subject"
          value={subject}
          onChange={(e) => setSubject(e.target.value)}
        />
      </div>
      <div>
        <textarea
          placeholder="Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </div>
      <div>
        <button type="submit">Send Message</button>
      </div>
    </form>
  );
};

export default CreateMessageScreen;
