import "./App.css";
import { useContext, useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { Store } from "./Store";
import MessagesBoxScreen from "./screens/MessagesBoxScreen";
import SignupScreen from "./screens/SignUpScreen";
import SigninScreen from "./screens/SignInScreen";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";
import { LinkContainer } from "react-router-bootstrap";
import Nav from "react-bootstrap/Nav";
import ProtectedRoute from "./components/ProtectedRoute";
import CreateMessageScreen from "./screens/CreateMessageScreen";
import { Tabs, Tab } from "react-bootstrap";

function App() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const [activeTab, setActiveTab] = useState("received"); // Default active tab

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const signoutHandler = () => {
    localStorage.removeItem("userInfo");
    ctxDispatch({ type: "USER_SIGNOUT" });
    window.location.href = "/signin";
  };

     return (
      <div className="App">
      <BrowserRouter>
        <ToastContainer position="top-center" limit={1} />
        <header>
          <Navbar bg="dark" variant="dark" expand="lg">
            <Container>
              <LinkContainer to="/">
                <Navbar.Brand>Message System</Navbar.Brand>
              </LinkContainer>
              {userInfo ? (
                <>
                  <Nav>
                    <LinkContainer to="/messages/received">
                      <Nav.Link>Received Messages</Nav.Link>
                    </LinkContainer>
                  </Nav>
                  <Nav>
                    <LinkContainer to="/messages/sent">
                      <Nav.Link>Sent Messages</Nav.Link>
                    </LinkContainer>
                  </Nav>
                  <Nav>
                    <LinkContainer to="/messages/read">
                      <Nav.Link>Read Messages</Nav.Link>
                    </LinkContainer>
                  </Nav>
                  <Nav>
                    <LinkContainer to="/messages/unread">
                      <Nav.Link>Unread Messages</Nav.Link>
                    </LinkContainer>
                  </Nav>
                  <Nav>
                    <LinkContainer to="/createmessage">
                      <Nav.Link>Create Message</Nav.Link>
                    </LinkContainer>
                  </Nav>
                  <Nav>
                    <LinkContainer to="/signin">
                      <Nav.Link onClick={signoutHandler}>Sign Out</Nav.Link>
                    </LinkContainer>
                  </Nav>
                </>
              ) : (
                <Nav>
                  <LinkContainer to="/signin">
                    <Nav.Link>Sign In</Nav.Link>
                  </LinkContainer>
                </Nav>
              )}
            </Container>
          </Navbar>
        </header>
        <main>
          <Routes>
            <Route path="/signin" element={<SigninScreen />} />
            <Route path="/" element={<SignupScreen />} />
            <Route
              path="/messages"
              element={
                userInfo ? (
                  <Tabs defaultActiveKey="received" animation={false} id="noanim-tab-example">
                    <Tab eventKey="received" title="Received Messages">
                      <MessagesBoxScreen messageType="received" />
                    </Tab>
                    <Tab eventKey="sent" title="Sent Messages">
                      <MessagesBoxScreen messageType="sent" />
                    </Tab>
                    <Tab eventKey="read" title="Read Messages">
                      <MessagesBoxScreen messageType="read" />
                    </Tab>
                    <Tab eventKey="unread" title="Unread Messages">
                      <MessagesBoxScreen messageType="unread" />
                    </Tab>
                  </Tabs>
                ) : (
                  <Navigate to="/signin" replace />
                )
              }
            />
            <Route
              path="/createmessage"
              element={
                userInfo ? (
                  <ProtectedRoute>
                    <CreateMessageScreen />
                  </ProtectedRoute>
                ) : (
                  <Navigate to="/signin" replace />
                )
              }
            />
          </Routes>
        </main>
        <footer>
          <div className="text-center">@2023 All rights reserved</div>
        </footer>
      </BrowserRouter>
    </div>
  );
