import React, { useContext, useEffect, useState } from "react";
import { Link, Navigate, useLocation, useNavigate } from "react-router-dom";
import { Helmet } from "react-helmet-async";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { getError } from "../utilis.js";
import { Store } from "../Store";
import axios from "axios";
import { toast } from "react-toastify";

const SigninScreen = () => {
//   const { search } = useLocation();
//   const redirectInUrl = new URLSearchParams(search).get("redirect");
//   const redirect = redirectInUrl ? redirectInUrl : "/";

  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  console.log(userInfo)

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post("/api/signin/", {
        username,
        email,
        password,
      });
      ctxDispatch({ type: "USER_SIGNIN", payload: data });
      localStorage.setItem("userInfo", JSON.stringify(data));
      navigate(`/messages/`);
    } catch (err) {
      toast.error(getError(err));
    }
  };

  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, redirect, userInfo]);

  return (
    <Container className="small-container">
      <Helmet>
        <title>Sign In</title>
      </Helmet>
      <h1 className="my-3">Sign In</h1>
      <Form onSubmit={submitHandler} className="d-flex flex-column align-items-center">
        <Form.Group className="mb-3 w-50" controlId="username">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="username"
            required
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3 w-50" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3 w-50" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <div className="mb-3">
          <Button type="submit">Sign In</Button>
        </div>
        <div className="mb-3">
          Forgot Password? <Link to={`/forgot-pwd`}>OY VEY</Link>
        </div>
        <div className="mb-3">
          New customer?{" "}
          <Link to={`/signup`}>Create your account</Link>
        </div>
      </Form>
    </Container>
  );
};

export default SigninScreen;
