import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { Store } from "../Store";
import { getError } from "../";
import { toast } from "react-toastify";



const CreateMessageScreen = () => {
  const [subject, setSubject] = useState("");
  const [content, setContent] = useState("");
  const [receiver, setReceiver] = useState("");
  const [users, setUsers] = useState([]);
  const { state } = useContext(Store);
  const { userInfo } = state;

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const {data} = await axios.post(
        'http://127.0.0.1:8000/api/addmessage/',
        { subject, content, receiver },
        { headers: { Authorization: `Bearer ${userInfo.access}` } }
      );

      console.log('New message created:', data);
      // Handle success and update message list if needed
    } catch (err) {
        toast.error(getError(err));
      console.error('Error creating message:', err.response.data);
      // Handle error if needed
    }
  };

//   useEffect(() => {
//     const fetchUsers = async () => {
//       try {
//         const {data} = await axios.get('http://127.0.0.1:8000/api/users/');
//         setUsers(data);
//       } catch (error) {
//         console.error('Error fetching users:', error.message);
//       }
//     };
//     fetchUsers();
//   }, []);

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="receiver">Select Receiver:</label>
        <input
          type="text"
          placeholder="receiver"
          value={receiver}
          onChange={(e) => setReceiver(e.target.value)}
        >
          
        </input>
      </div>
      <div>
        <input
          type="text"
          placeholder="Subject"
          value={subject}
          onChange={(e) => setSubject(e.target.value)}
        />
      </div>
      <div>
        <textarea
          placeholder="Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </div>
      <div>
        <button type="submit">Send Message</button>
      </div>
    </form>
  );
};

export default CreateMessageScreen;
